import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const Nav = () => {
  return (
    <div className="header-container">
      <div className="logo-wrapper">
        <h3>Careers</h3>
      </div>
      <div className="nav-wrapper">
        <ul>
          <li>
            <div className="dropdown">
              <span className="dropspan">Products</span>
              <div className="dropdown-content">
                <ul>
                  <li className="left-list">
                    <span>COMPUTER VISION</span>
                  </li>
                </ul>
              </div>
            </div>
          </li>
          <li>
            <span>Solution</span>
          </li>
          <li>
            <span>Pricing</span>
          </li>
          <li>
            <span>Resources</span>
          </li>
          <li>
            <span>Company</span>
          </li>
        </ul>
      </div>
      <div className="actions-wrapper">
        <ul>
          <li>
            <span>Docs</span>
          </li>
          <li>
            <span>
              Log In <FontAwesomeIcon icon="angle-right" />
            </span>
          </li>
        </ul>
      </div>
      <style jsx>
        {`
          .actions-wrapper span:hover {
            opacity: 0.5;
          }
          .nav-wrapper span:hover {
            opacity: 0.5;
          }

          .nav-wrapper ul {
            display: flex;
            flex-direction: row;
            list-style: none;
            padding: 0;
            margin: 0;
          }

          .nav-wrapper li {
            padding: 0px 10px;
          }

          .left-list {
            background-color: white;
            height: 100%;
            width: 100%;
          }
          .actions-wrapper ul {
            display: flex;
            flex-direction: row;
            list-style: none;
            padding: 0;
            margin: 0;
            padding-left: 40px;
          }

          .actions-wrapper li {
            padding-left: 20px;
          }

          .header-container {
            width: 100vw;
            height: 100px;
            background-color: white;
            display: grid;
            grid-template-columns: repeat(3, 1fr);
            margin: auto;
            text-align: center;
            font-size: 16px;
            padding: 20px 40px 20px 40px;
          }

          .header-container span {
            transition: 0.8s cubic-bezier(0.2, 0.8, 0.2, 1);
            cursor: pointer;
          }
          /* The container <div> - needed to position the dropdown content */
          .dropdown {
            position: relative;
            transition: 2s cubic-bezier(0.2, 0.8, 0.2, 1);
          }

          /* Dropdown Content (Hidden by Default) */
          .dropdown-content {
            display: none;
            position: fixed;
            background-color: white;
            height: 400px;
            width: 300px;
            box-shadow: 0 10px 20px rgba(0, 0, 0, 0.25);
            border-radius: 10px;
            margin-top: 15px;
            z-index: 1;
          }

          /* Show the dropdown menu on hover */
          .dropdown:hover .dropdown-content {
            display: block;
          }
        `}
      </style>
    </div>
  );
};

export default Nav;
