const Hero = () => {
  return (
    <div className="hero">
      <img src="../static/images/hero-logo.svg" alt="hero logo" />
      <h1>Scale is growing. Grow with us.</h1>

      <p>
        We believe that the transition from traditional software to AI <br /> is
        one of the most important shifts of our time.{" "}
      </p>
      <button>View our open position</button>
      <style jsx>{`
        .hero {
          height: 100vh;
          text-align: center;
        }

        .hero button {
          background: #ed72e2;
          box-shadow: 0px 5px 20px rgba(0, 0, 0, 0.2);
          border-radius: 8px;
          font-family: IBM Plex Sans;
          font-style: normal;
          font-weight: 600;
          font-size: 15px;
          line-height: 19px;
          display: flex;
          align-items: center;
          text-align: center;
          letter-spacing: 1.3px;
          text-transform: uppercase;

        }
      `}</style>
    </div>
  );
};

export default Hero;
