import Card from "../components/DeparmentCard";
const Hero = () => {
  return (
    <div className="hero">
      <img src="../static/images/hero-logo.svg" alt="hero logo" />
      <h1>Scale is growing. Grow with us.</h1>

      <p>
        We believe that the transition from traditional software to AI <br /> is
        one of the most important shifts of our time.{" "}
      </p>
      <button>View our open position</button>
      <div className="img-container">
        <img width="90%" src="../static/images/hero-img.jpg" alt="hero logo" />
        <div className="teams-banner">
          <h1>POSITIONS BY DEPARtMENT</h1>
          <ul className="departments-list">
          <Card name="Static Analysis"
          logo={require('../images/static-analysis.svg')}
          tech="Java"></Card>
            <li>
              <img src="../static/images/sales.svg" alt="hero logo" />
              <h4>Sales</h4>
            </li>
            <li>
              <img src="../static/images/business.svg" alt="hero logo" />
              <h4>Business</h4>
            </li>
            <li>
              <img src="../static/images/finance.svg" alt="hero logo" />
              Finance
            </li>
            <li>
              <img src="../static/images/marketing.svg" alt="hero logo" />
              Marketing
            </li>
            <li>
              <img src="../static/images/operations.svg" alt="hero logo" />
              Operations
            </li>
            <li>
              <img src="../static/images/people.svg" alt="hero logo" />
              People
            </li>
            <li>
              <img src="../static/images/product.svg" alt="hero logo" />
              Product
            </li>
            <li>
              <img src="../static/images/engineering.svg" alt="hero logo" />
              Engineering
            </li>
          </ul>
        </div>
      </div>
      <style jsx>{`
        .hero {
          text-align: center;
          align-items: center;
          margin-top: 40px;
          position: relative;
        }
        .hero button {
          background: #ed72e2;
          box-shadow: 0px 5px 20px rgba(0, 0, 0, 0.2);
          border-radius: 8px;
          font-style: normal;
          font-weight: 600;
          font-size: 15px;
          line-height: 19px;
          letter-spacing: 1.3px;
          text-transform: uppercase;
          color: white;
          padding: 15px 15px;
          cursor: pointer;
        }

        .img-container {
          margin-top: 40px;
          z-index: 0;
        }

        .teams-banner {
          width: 80vw;
          height: 200px;
          background-color: white;
          z-index: 1;
          border-radius: 10px;
          box-shadow: 0 10px 20px rgba(0, 0, 0, 0.25);
          position: absolute;
          left: 50%;
          transform: translate(-50%, -50%);
        }
        .teams-banner h1 {
          font-size: 14px;
          border-bottom: 1px solid grey;
          padding-bottom: 10px;
          text-transform: uppercase;
        }
      `}</style>
    </div>
  );
};

export default Hero;
