import React from "react";

const ProductCard = props => {
  return (
    <div className="pc-wrapper">
      <h1 className="p-name">{props.productName}</h1>
      <p className="p-des">{props.productDes}</p>
      <ul>
        {props.productList.map(feature => (
          <li key={feature.id}><span></span>feature.title}</li>
        ))}
      </ul>
      <style global jsx>{`
        .pc-wrapper {
          margin: 20px;
          padding: 0 20px;
          color: black;
          background-color: white;
          box-shadow: 5px 10px 10px rgba(0, 0, 0, 0.25);
          border-radius: 16px;
        }
      `}</style>
    </div>
  );
};

export default ProductCard;
