import react from "react";

const PricingToggle = () => {
  return (
    <div className="m-container">
      <h2>PLANS</h2>
      <div className="pricing-toggle-wrapper">
        <span id="standard">Standard</span>
        <span id="enterprise">Enterprise</span>
      </div>

      <style jsx>{`
        .m-container {
          display: grid;
          justify-content: center;
          align-items: center;
        }
        .pricing-toggle-wrapper {
          display: flex;
          justify-content: center;
          align-items: center;
          width: 485px;
          height: 70px;
          background: white;
          border: 1px solid #c4c4c4;
          border-radius: 40px;
          justify-content: center;
          display: grid;
          grid-template-columns: repeat(2, 1fr);
          padding: 0px 10px;
        }

        .pricing-toggle-wrapper span {
          color: black;
          font-size: 28px;
          font-weight: 400;
        }

        .pricing-toggle-wrapper #enterprise {
          padding: 10px 30px;
          background: #fd62e9;
          border-radius: 40px;
          display: flex;
          justify-content: center;
          align-items: center;
          border: 1px solid black;
        }

        .pricing-toggle-wrapper #standard {
          padding: 10px 30px;
          background: white;
          border-radius: 40px;
          display: flex;
          justify-content: center;
          align-items: center;
        }

        h2 {
          color: black;
          text-align: center;
          font-size: 50px;
          font-weight: 300;
        }
      `}</style>
    </div>
  );
};

export default PricingToggle;
