import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const Header = () => {
  return (
    <div className="header-container">
      <div className="logo-wrapper">
        <img src="../static/images/logo.svg" alt="my image" />
      </div>
      <div className="nav-wrapper">
        <ul>
          <li>
            <span>Products</span>
          </li>
          <li>
            <span>Solution</span>
          </li>
          <li>
            <span>Pricing</span>
          </li>
          <li>
            <span>Resources</span>
          </li>
          <li>
            <span>Company</span>
          </li>
        </ul>
      </div>
      <div className="actions-wrapper">
        <ul>
          <li>
            <span>Docs</span>
          </li>
          <li>
            <span>
              Log In <FontAwesomeIcon icon="angle-right" />
            </span>
          </li>
        </ul>
      </div>
      <style jsx>
        {`
        .logo-wrapper span:hover{
            opacity: 0.5;
        }
        .nav-wrapper span:hover{
            opacity: 0.5;
        }

        .nav-wrapper ul{
            display: flex;
            flex-direction: row;
            list-style:none;
            padding:0;
            margin:0;
        }

        .nav-wrapper li{
            padding: 0px 10px;
        }

        .actions-wrapper ul{
            display: flex;
            flex-direction: row;
            list-style:none;
            padding:0;
            margin:0;
            padding-left: 40px;
        }

        .actions-wrapper li{
            padding-left:20px;
        }

        .header-container {
            width:100vw;
            height:100px;
            background-color: white;
            display: grid;
            grid-template-columns: repeat(3, 1fr);
            margin: auto;
            text-align: center;
            font-size: 16px;
            padding:  20px 40px 20px 40px;
        }

        .header-container span{
            transition: 0.8s cubic-bezier(0.2, 0.8, 0.2, 1);
        `}
      </style>
    </div>
  );
};

export default Header;
