import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const Header = () => {
  return (
    <div className="header-container">
      <div className="logo-wrapper">
        <img src="../static/images/logo.svg" alt="my image" />
      </div>
      <div className="nav-wrapper">
        <ul>
          <li>
            <div className="dropdown">
              <span className="dropspan">Products</span>
              <div className="dropdown-content">
                <h2 ></h2>
                <ul>
                  <li>
                    <span>Solution</span>
                  </li>
                </ul>
              </div>
            </div>
          </li>
          <li>
            <span>Solution</span>
          </li>
          <li>
            <span>Pricing</span>
          </li>
          <li>
            <span>Resources</span>
          </li>
          <li>
            <span>Company</span>
          </li>
        </ul>
      </div>
      <div className="actions-wrapper">
        <ul>
          <li>
            <span>Docs</span>
          </li>
          <li>
            <span>
              Log In <FontAwesomeIcon icon="angle-right" />
            </span>
          </li>
        </ul>
      </div>
      <style jsx>
        {`
          .actions-wrapper span:hover {
            opacity: 0.5;
          }
          .nav-wrapper span:hover {
            opacity: 0.5;
          }

          .nav-wrapper ul {
            display: flex;
            flex-direction: row;
            list-style: none;
            padding: 0;
            margin: 0;
          }

          .nav-wrapper li {
            padding: 0px 10px;
          }

          .actions-wrapper ul {
            display: flex;
            flex-direction: row;
            list-style: none;
            padding: 0;
            margin: 0;
            padding-left: 40px;
          }

          .actions-wrapper li {
            padding-left: 20px;
          }

          .header-container {
            width: 100vw;
            height: 100px;
            background-color: white;
            display: grid;
            grid-template-columns: repeat(3, 1fr);
            margin: auto;
            text-align: center;
            font-size: 16px;
            padding: 20px 40px 20px 40px;
          }

          .header-container span {
            transition: 0.8s cubic-bezier(0.2, 0.8, 0.2, 1);
            cursor: pointer;
          }
          /* The container <div> - needed to position the dropdown content */
          .dropdown {
            position: relative;
            transition: 6s cubic-bezier(0.2, 0.8, 0.2, 1);
            border: 1px solid black;
          }

          /* Dropdown Content (Hidden by Default) */
          .dropdown-content {
            display: none;
            position: absolute;
            background-color: #f1f1f1;
            height: 500px;
            width: 500px;
            box-shadow: 0px 8px 16px 0px rgba(0, 0, 0, 0.2);
            border-radius: 10px;
            margin-top: 15px;
            z-index: 1;
          }

          /* Show the dropdown menu on hover */
          .dropdown:hover .dropdown-content {
            display: block;
          }
        `}
      </style>
    </div>
  );
};

export default Header;
